INSERT INTO `library_eidikotiteserg` (`id`, `name`, `map`) VALUES
(1, 'Διοικητικό προσωπικό', ''),
(2, 'Εργατοτεχνίτες', 'Φόρμα, Επενδυτής, Αδιάβροχο, Μπότες δερμάτινες με έλασμα ή Γαλότσες με εσωτερική επένδυση, Γυαλιά ηλίου, Καπέλο, Κράνος κατά περίπτωση, Μάσκα προστασίας αναπνοής, σκούφος κεφαλής'),
(3, 'Συντηρητές', 'Φόρμα, Επενδυτής, Αδιάβροχο, Γάντια δερμάτινα εργασίας, Μπότες δερμάτινες με έλασμα, Γυαλιά προστασίας,  Κράνος'),
(4, 'Συγκολητές', 'Φόρμα, Επενδυτής, Αδιάβροχο, Γάντια δερμάτινα εργασίας διηλεκτρικής αντοχής, Μπότες δερμάτινες με έλασμα και διηλεκτρική αντοχή, Γυαλιά προστασίας ή μάσκα προστασίας, Κράνος με ασπίδιο'),
(5, 'Ηλεκτρολόγοι', 'Φόρμα, Επενδυτής, Αδιάβροχο, Ανακλαστικό γιλέκο, Γάντια δερμάτινα εργασίας διηλεκτρικής αντοχής, Μπότες δερμάτινες με έλασμα και διηλεκτρική αντοχή, Γυαλιά προστασίας, Κράνος με ασπίδιο'),
(6, 'Υδραυλικοί-Υδρονομείς', 'Φόρμα, Επενδυτής, Αδιάβροχο, Γαλότσες με εσωτερική επένδυση, Καπέλο, Επιγονατίδες, Ποδιά αδιάβροχη'),
(7, 'Οδοκαθαριστές-Εργ. Καθαριότητας', 'Φόρμα, Επενδυτής, Αδιάβροχο, Ανακλαστικό γιλέκο,  Γαλότσες με εσωτερική επένδυση, Καπέλο, Μάσκα προστασίας αναπνοής'),
(8, 'Οδηγοί', 'Φόρμα, Επενδυτής, Αδιάβροχο, Ανακλαστικό γιλέκο, Μπότες δερμάτινες με έλασμα, Γαλότσες με εσωτερική επένδυση, Γυαλιά ηλίου, Καπέλο, Μάσκα προστασίας αναπνοής'),
(9, 'Χειριστές Μ.Ε.', 'Φόρμα, Επενδυτής, Αδιάβροχο, Ανακλαστικό γιλέκο, Μπότες δερμάτινες με έλασμα, Γαλότσες με εσωτερική επένδυση, Γυαλιά ηλίου, Κράνος, Μάσκα προστασίας αναπνοής'),
(10, 'Δημοτική αστυνομία', 'Φόρμα, Επενδυτής, Αδιάβροχο, Ανακλαστικό γιλέκο, Μπότες δερμάτινες με έλασμα, Γαλότσες με εσωτερική επένδυση, Γυαλιά ηλίου, Καπέλο'),
(11, 'Σχολικοί φύλακες', 'Φόρμα, Επενδυτής, Αδιάβροχο, Ανακλαστικό γιλέκο, Μπότες δερμάτινες με έλασμα, Γαλότσες με εσωτερική επένδυση, Γυαλιά ηλίου, Καπέλο'),
(12, 'Μηχανικοί - Τεχνικών έργων', 'Φόρμα, Επενδυτής, Αδιάβροχο, Γάντια δερμάτινα εργασίας, Μπότες δερμάτινες με έλασμα, Γυαλιά προστασίας, Κράνος'),
(13, 'Άλλη ειδικότητα', 'Κατά περίπτωση');
INSERT INTO `library_entypa` (`id`, `type`, `name`, `text`) VALUES
(1, 1, 'Έντυπο 1', '<p align="right">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; <u>ΕΝΤΥΠΟ 1</u></p>\r\n\r\n<p align="center">ΓΝΩΣΤΟΠΟΙΗΣΗ ΑΝΑΘΕΣΗΣ ΚΑΘΗΚΟΝΤΩΝ ΤΕΧΝΙΚΟΥ ΑΣΦΑΛΕΙΑΣ / ΓΙΑΤΡΟΥ ΕΡΓΑΣΙΑΣ</p>\r\n\r\n<p align="right"><strong>Προς ΚΕΠΕΚ/ΤΜΗΜΑ</strong>&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;</p>\r\n\r\n<p align="right"><strong>Αρ. Πρωτ.</strong>&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;.*</p>\r\n\r\n<p><span style="font-size:12px;"><strong>ΣΤΟΙΧΕΙΑ ΕΠΙΧΕΙΡΗΣΗΣ</strong></span></p>\r\n\r\n<p><span style="font-size:12px;"><strong>Επωνυμία Επιχείρησης</strong> ... {ENTYPA_ONOMA}</span> ...</p>\r\n\r\n<p><span style="font-size:12px;"><strong>Α.Φ.Μ</strong> ... {ENTYPA_AFM} ... <strong>Δ.Ο.Υ</strong> ... {ENTYPA_DOY}</span> ...</p>\r\n\r\n<p><span style="font-size:12px;"><strong>Διεύθυνση</strong> .... {ENTYPA_ADDRESS} .... <strong>Τ.Κ</strong>&hellip;&hellip;&hellip;&hellip;.</span></p>\r\n\r\n<p><span style="font-size:12px;"><strong>Δήμος</strong>&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;.&hellip;&hellip;.<strong>Τηλέφωνα:</strong>.... {ENTYPA_TEL} ....Fax:&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;</span></p>\r\n\r\n<p><span style="font-size:12px;"><strong>Δραστηριότητα Επιχείρησης :</strong> .... {ENTYPA_DRASTIRIOTITA}</span> ....</p>\r\n\r\n<p><span style="font-size:12px;"><strong>Κατηγορία Επικινδυνότητας:</strong>&hellip;&hellip;&hellip;&hellip;..*&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>ΣΤΑΚΟΔ:</strong>&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;..*</span></p>\r\n\r\n<p><span style="font-size:12px;"><strong>Νόμιμος Εκπρόσωπος</strong> ... {ENTYPA_YP_ONOMA}</span> ...</p>\r\n\r\n<p><span style="font-size:12px;"><strong>Σύνολο εργαζομένων :</strong> ... {ENTYPA_ERG} ... &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Άνδρες</strong> ... {ENTYPA_ERGMALE} ... &nbsp;&nbsp;&nbsp; <strong>Γυναίκες</strong> ... {ENTYPA_ERGFEMALE} ... <strong>Ανήλικοι</strong> ... {ENTYPA_ERG18}</span> ...</p>\r\n\r\n<p style="margin-left:180.0pt;"><span style="font-size:12px;"><strong>Διοικητικό</strong> ... {ENTYPA_ERGDIOIK} ...&nbsp; <strong>Εργατοτεχνικό</strong> ... {ENTYPA_ERGTEXN}</span> ...</p>\r\n\r\n<table border="1" cellpadding="0" cellspacing="0" height="408" width="1437">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p style="text-align: center;"><span style="font-size:10px;">ΣΤΟΙΧΕΙΑ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;"><span style="font-size:10px;">ΤΕΧΝΙΚΟΣ ΑΣΦΑΛΕΙΑΣ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;"><span style="font-size:10px;">ΓΙΑΤΡΟΣ ΕΡΓΑΣΙΑΣ</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">Α.Φ.Μ.</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">{ENTYPA_TAAFM}</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΟΝΟΜΑΤΕΠΩΝΥΜΟ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">{ENTYPA_TAONOMA}</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΟΝΟΜΑ ΠΑΤΡΟΣ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΑΡ. ΠΡΩΤ.- ΗΜ/ΝΙΑ 1ης ΚΑΤΑΘ. ΔΙΚΑΙΟΛΟΓΗΤΙΚΩΝ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΕΙΔΙΚΟΤΗΤΑ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">{ENTYPA_TAEIDIKOTITA}</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΕΠΙΠΕΔΟ ΣΠΟΥΔΩΝ ΤΑ(ΠΕ,ΤΕ,ΔΕ)</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;"><span style="font-size:10px;">Δε συμπληρώνεται από τους Γ.Ε.</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΑΡ.ΜΗΤΡΩΟΥ Τ.Ε.Ε. ΤΑ&nbsp;</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;"><span style="font-size:10px;">Δε συμπληρώνεται από τους Γ.Ε</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΣΧΕΣΗ ΕΡΓΑΣΙΑΣ : ΕΡΓΟΔΟΤΗΣ (Τ.Α.) / ΥΠΑΛΛΗΛΟΣ /ΕΞ. ΣΥΝΕΡΓΑΤΗΣ / ΕΞΥΠΠ(ΕΠΩΝ.)</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΩΡΕΣ ΑΝΑ ΕΤΟΣ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">{ENTYPA_TAWRES}</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΠΡΟΓΡΑΜΜΑ ΕΡΓΑΣΙΑΣ (ΗΜ/ΝΙΑ &amp; ΩΡΕΣ)</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">{ENTYPA_TAPROGRAM}</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΗΜ/ΝΙΑ ΛΗΞΗΣ ΣΥΜΒΑΣΗΣ ΕΞΩΤ. ΣΥΝΕΡΓΑΤΗ / ΕΞΥΠΠ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">{ENTYPA_TAEND}</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style="font-size:10px;">ΣΥΝΟΛΟ ΩΡΩΝ ΑΠΑΣΧΟΛΗΣΗΣ ΣΕ ΑΛΛΕΣ ΕΠΙΧΕΙΡΗΣΕΙΣ</span></p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">{ENTYPA_TAWRES_OTHER}</p>\r\n			</td>\r\n			<td>\r\n			<p style="text-align: center;">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p align="right"><strong>Ημερομηνία&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;.</strong></p>\r\n\r\n<p>Μόνο για ΕΞΥΠΠ &ndash; Για την ακρίβεια των στοιχείων</p>\r\n\r\n<table align="left" border="1" cellpadding="0" cellspacing="0" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p align="center"><span style="font-size:10px;">ΕΞΥΠΠ</span></p>\r\n			</td>\r\n			<td>\r\n			<p align="center"><span style="font-size:10px;">ΤΕΧΝ. ΑΣΦΑΛΕΙΑΣ</span></p>\r\n			</td>\r\n			<td>\r\n			<p align="center"><span style="font-size:10px;">ΓΙΑΤΡΟΣ ΕΡΓΑΣΙΑΣ</span></p>\r\n			</td>\r\n			<td>\r\n			<p align="center"><span style="font-size:10px;">Για την επιχείρηση</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p align="center">&nbsp;</p>\r\n\r\n			<p align="center">&nbsp;</p>\r\n\r\n			<p align="center"><span style="font-size:10px;">Υπογραφή - Σφραγίδα</span></p>\r\n			</td>\r\n			<td>\r\n			<p align="center">&nbsp;</p>\r\n\r\n			<p align="center">&nbsp;</p>\r\n\r\n			<p align="center"><span style="font-size:10px;">Υπογραφή - Σφραγίδα</span></p>\r\n			</td>\r\n			<td>\r\n			<p align="center">&nbsp;</p>\r\n\r\n			<p align="center">&nbsp;</p>\r\n\r\n			<p align="center"><span style="font-size:10px;">Υπογραφή - Σφραγίδα</span></p>\r\n			</td>\r\n			<td>\r\n			<p align="center">&nbsp;</p>\r\n\r\n			<p align="center">&nbsp;</p>\r\n\r\n			<p align="center"><span style="font-size:10px;">Υπογραφή - Υπευθύνου</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n'),
(2, 2, 'Έντυπο 2', '<table border="0" cellpadding="0" cellspacing="0" style="width:1019px;" width="1019">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="10" nowrap="nowrap" style="width:1019px;height:19px;">\r\n			<p align="center"><strong><u>ΓΝΩΣΤΟΠΟΙΗΣΗ ΑΝΑΘΕΣΗΣ ΚΑΘΗΚΟΝΤΩΝ ΤΕΧΝΙΚΟΥ ΑΣΦΑΛΕΙΑΣ / ΓΙΑΤΡΟΥ ΕΡΓΑΣΙΑΣ (ΕΝΤΥΠΟ 2)</u></strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="6" nowrap="nowrap" style="width:466px;height:25px;">\r\n			<p><strong>ΠΡΟΣ:&nbsp; ΚΕΠΕΚ/ΤΜΗΜΑ:</strong>&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:30px;height:25px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:186px;height:25px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:47px;height:25px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:291px;height:25px;">\r\n			<p align="right">&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td nowrap="nowrap" style="width:208px;height:20px;">\r\n			<p><strong>ΑΡ. ΠΡΩΤ.:</strong>&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;*</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:49px;height:20px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:49px;height:20px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:53px;height:20px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:49px;height:20px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:57px;height:20px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:30px;height:20px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:186px;height:20px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:47px;height:20px;">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td nowrap="nowrap" style="width:2